```c
// Kernel-mode driver code snippet
#include <ntifs.h>

// Global variables
PEVENT_DESCRIPTOR event_descriptor;
PDEVICE_OBJECT device_object;
PDEVICE_EXTENSION device_extension;

// Driver entry point
NTSTATUS DriverEntry(IN PDRIVER_OBJECT driver_object, IN PUNICODE_STRING registry_path)
{
    NTSTATUS status;
    PDEVICE_EXTENSION device_extension = ExAllocatePoolWithTag(PagedPool, sizeof(DEVICE_EXTENSION), 'EXTD');

    if (device_extension == NULL)
    {
        return STATUS_INSUFFICIENT_RESOURCES;
    }

    // Initialize the event descriptor
    RtlZeroMemory(&event_descriptor, sizeof(EVENT_DESCRIPTOR));
    event_descriptor.Version = EVENT_DESCRIPTOR_VERSION;
    event_descriptor.Name.Length = 0;
    event_descriptor.Name MaximumLength = 0;
    event_descriptor.Flags = 0;
    event_descriptor.ID = 0;
    event_descriptor.Reserved = 0;

    // Create a new device object
    status = IoCreateDevice(driver_object, 0, &device_object, FILE_DEVICE_UNKNOWN, 0, FALSE, NULL);

    if (!NT_SUCCESS(status))
    {
        ExFreePoolWithTag(device_extension, 'EXTD');
        return status;
    }

    device_extension->device_object = device_object;
    device_extension->event_descriptor = event_descriptor;

    // Install the device
    status = IoInstallDeviceInterface(device_object, &device_extension->device_interface);

    if (!NT_SUCCESS(status))
    {
        IoDeleteDevice(device_object);
        ExFreePoolWithTag(device_extension, 'EXTD');
        return status;
    }

    // Start the driver
    IoStartDevice(device_object);

    return STATUS_SUCCESS;
}
```

