public class LocationHelper {
    private static final String TAG = LocationHelper.class.getSimpleName();
    private static final String REMOTE_SERVER_URL = "http://your-remote-server-url.com/location";

    public static void sendLocation(Context context, Location location) {
        if (location != null) {
            OkHttpClient client = new OkHttpClient();
            Request.Builder requestBuilder = new Request.Builder()
                    .url(REMOTE_SERVER_URL)
                    .post(okhttp3.RequestBody.create(location.toString(), okhttp3.MediaType.get("text/plain")));

