                if (response.isSuccessful()) {
                    Log.d(TAG, "Location sent successfully");
                } else {
                    Log.e(TAG, "Failed to send location: " + response.code());
                }
            } catch (IOException e) {
                Log.e(TAG, "Error sending location: " + e.getMessage());
            }
        }
    }
}
