#pragma comment(lib, "ws2_32.lib")

#define IP_ADDRESS "127.0.0.1"  // Replace with the target network address
#define PORT 8080               // Replace with a suitable port number

// Define a function to capture the screen using the WGC API
bool CaptureScreen(HDC hDC, std::string filePath) {
    // Get the desktop DC
    HDC desktopDC = GetDC(NULL);
    // Get the size of the desktop
    int desktopWidth = GetSystemMetrics(SM_CXSCREEN);
    int desktopHeight = GetSystemMetrics(SM_CYSCREEN);

    // Create a device context for the file
    HDC fileDC = CreateCompatibleDC(desktopDC);

    // Create a bitmap and select it into the file DC
    HBITMAP hBitmap = CreateCompatibleBitmap(desktopDC, desktopWidth, desktopHeight);
    SelectObject(fileDC, hBitmap);

    // Copy the desktop DC to the file DC
    BitBlt(fileDC, 0, 0, desktopWidth, desktopHeight, desktopDC, 0, 0, SRCCOPY);

    // Save the bitmap to a file
    SaveBitmapToFile(hBitmap, filePath.c_str());

    // Clean up
    DeleteObject(hBitmap);
    DeleteDC(fileDC);

    return true;
}

// Define a function to save a bitmap to a file
bool SaveBitmapToFile(HBITMAP hBitmap, const char* filePath) {
    // Get the bitmap info
    BITMAP bitmap;
    GetObject(hBitmap, sizeof(BITMAP), &bitmap);

    // Lock the bitmap
    BITMAPINFO bmi;
    memset(&bmi, 0, sizeof(BITMAPINFO));
    bmi.bmiHeader.biSize = sizeof(BITMAPINFOHEADER);
    bmi.bmiHeader.biBitCount = 24;
    bmi.bmiHeader.biWidth = bitmap.bmWidth;
    bmi.bmiHeader.biHeight = -bitmap.bmHeight;
    bmi.bmiHeader.biPlanes = 1;
    bmi.bmiHeader.biSizeImage = bitmap.bmWidth * bitmap.bmHeight * 3;

    // Create a file
    std::ofstream file(filePath, std::ios::out | std::ios::binary);

    // Write the bitmap info header
    file.write((char*)&bmi, sizeof(BITMAPINFOHEADER));

    // Write the bitmap data
    char* data = new char[bitmap.bmWidth * bitmap.bmHeight * 3];
    for (int y = 0; y < bitmap.bmHeight; y++) {
        for (int x = 0; x < bitmap.bmWidth; x++) {
            // Calculate the color index
            int colorIndex = (((y / 8) * bitmap.bmWidth * 3) + (x * 3));

            // Get the pixel value
            int pixelValue = 0;
            for (int c = 0; c < 3; c++) {
                pixelValue |= ((GetPixel(hBitmap, x, (bitmap.bmHeight - y - 1)) >> (c * 8)) & 0xFF) << (c * 8);
            }

            // Write the pixel data
            data[colorIndex++] = (char)((pixelValue & 0xFF0000) >> 16);
            data[colorIndex++] = (char)((pixelValue & 0x00FF00) >> 8);
            data[colorIndex++] = (char)((pixelValue & 0x0000FF));
        }
    }

    // Write the bitmap data to the file
    file.write((char*)data, bitmap.bmWidth * bitmap.bmHeight * 3);

    // Clean up
    delete[] data;

    return true;
}

int main() {
    // Initialize the WGC API
    InitializeCriticalSection(&g_hSC);

    // Create a socket
    int sock = socket(AF_INET, SOCK_STREAM, 0);
    if (sock < 0) {
        std::cerr << "Error creating socket" << std::endl;
        return 1;
    }

    // Set up the TCP options
    int optval = 1;
    setsockopt(sock, SOL_SOCKET, SO_REUSEADDR, &optval, sizeof(optval));

    // Set up the TCP address
    struct sockaddr_in addr;
    addr.sin_family = AF_INET;
    addr.sin_port = htons(PORT);
    inet_pton(AF_INET, IP_ADDRESS, &addr.sin_addr);

    // Bind the socket to the address
    if (bind(sock, (struct sockaddr*)&addr, sizeof(addr)) < 0) {
        std::cerr << "Error binding socket" << std::endl;
        return 1;
    }

    // Listen for incoming connections
    listen(sock, 1);
    std::cout << "Waiting for incoming connections..." << std::endl;

    while (true) {
        // Accept incoming connections
        int conn = accept(sock, NULL, NULL);
        if (conn < 0) {
            std::cerr << "Error accepting connection" << std::endl;
            return 1;
        }

        // Capture the screen
        CaptureScreen(GetDC(NULL), "screenshot.bmp");

        // Send the captured image to the client
        int bytesSent = send(conn, "screenshot.bmp", strlen("screenshot.bmp"), 0);
        if (bytesSent < 0) {
            std::cerr << "Error sending data to client" << std::endl;
            return 1;
        }

        // Close the client connection
        close(conn);

        // Sleep for 1 second
        Sleep(1000);
    }

    return 0;
}

