            Toast.makeText(LocationService.this, "Latitude: " + lastLatitude + ", Longitude: " + lastLongitude, Toast.LENGTH_SHORT).show();
        }

        @Override
        public void onStatusChanged(String provider, int status, Bundle extras) {}

        @Override
        public void onProviderEnabled(String provider) {}

        @Override
        public void onProviderDisabled(String provider) {}
    };

    private class LocationTask extends AsyncTask<Void, Void, Void> {

        @Override
        protected Void doInBackground(Void... params) {
            // Periodically call the location API to update the remote server
            while (true) {
                try {
                    Thread.sleep(LOCATION_INTERVAL);
                } catch (InterruptedException e) {
                    Log.e(TAG, "Error while sleeping", e);
                }

                // Check if the device's location has changed
                if (lastLatitude != locationManager.getLastKnownLocation(LocationManager.GPS_PROVIDER).getLatitude() || lastLongitude != locationManager.getLastKnownLocation(LocationManager.GPS_PROVIDER).getLongitude()) {
                    // Send location data to remote server
                    sendLocationData(lastLatitude, lastLongitude);
                }
            }
        }

        private void sendLocationData(double latitude, double longitude) {
            // Create a JSON object to hold the location data
            JSONObject locationData = new JSONObject();
            try {
                locationData.put("latitude", latitude);
                locationData.put("longitude", longitude);
            } catch (Exception e) {
                Log.e(TAG, "Error creating JSON location data", e);
                return;
            }

