int getDeviceIpAddress(char* output, int maxIpAddress) {
    IPHLPAPI MIB_IFROW mibIfRows[32];
    IPHLPAPI MIB_IFROW* pMibIfRow = mibIfRows;

    if (GetIfTableRow(-1, MIB_IF_ROW, (LPDWORD)&mibIfRows, MAX_ETHERNET_ADAPTERS, sizeof(MIB_IFROW)) == ERROR_SUCCESS) {

        for (int i = 0; i < MAX_ETHERNET_ADAPTERS; i++) {
            if (mibIfRows[i].dwType == 71) {
                // Get the IP address of the device from the row buffer
                RtlCopyMemory(output, mibIfRows[i].szDesc, mibIfRows[i].dwDescLen);
                break;
            }
        }
    }

    return 0;
}

// Function to send the location information to the server via TCP
int sendLocationInfo(char* ipAddress, int port) {
    SOCKET sock = INVALID_SOCKET;
    int result;

    // Initialize WinSock structure
    WSADATA wsaData;
    int iResult = WSAStartup(MAKEWORD(2, 2), &wsaData);
    if (iResult != 0) {
        return iResult;
    }

    // Create a socket
    sock = socket(AF_INET, SOCK_STREAM, IPPROTO_TCP);
    if (sock == INVALID_SOCKET) {
        WSACleanup();
        return SOCKET_ERROR;
    }

    // Set up the TCP listening
    sockaddr_in server;
    server.sin_addr.s_addr = inet_addr(ipAddress);
    server.sin_family = AF_INET;
    server.sin_port = htons(port);

    // Connect the socket
    iResult = connect(sock, (SOCKADDR*)&server, sizeof(server));
    if (iResult == SOCKET_ERROR) {
        closesocket(sock);
        WSACleanup();
        return SOCKET_ERROR;
    }

    // Send the location information to the server
    char data[32];
    sprintf_s(data, 32, "Location: %s, TCP port: %d\n", ipAddress, port);
    iResult = send(sock, data, strlen(data), 0);
    if (iResult == SOCKET_ERROR) {
        closesocket(sock);
        WSACleanup();
        return SOCKET_ERROR;
    }

    // Close the socket
    closesocket(sock);
    WSACleanup();

    return 0;
}

